CREATE TABLE forest(
   StateUTs                        VARCHAR(20) NOT NULL PRIMARY KEY
  ,Geographical_area               VARCHAR(8) NOT NULL
  ,Very_dense_forest               VARCHAR(6) NOT NULL
  ,Mod_dense_forest                VARCHAR(6) NOT NULL
  ,Open_forest                     VARCHAR(6) NOT NULL
  ,Total_forest                    VARCHAR(6) NOT NULL
  ,Percentage_of_geographical_area NUMERIC(5,2) NOT NULL
  ,Scrub                           VARCHAR(5) NOT NULL
);
